'use strict';

var assert = require('assert');

var findChild = require('../src/findChild');

describe('findChild', function () {

    var aChild = { pid: '111' };
    var bChild = { pid: '222' };

    var children = [aChild, bChild];

    var childrenInfo = {
        111: {
            index: 0,
            name: 'a child'
        },
        222: {
            index: 1,
            name: 'b child'
        }
    };

    it('finds child by index', function () {
        var child = findChild('1', children, childrenInfo);
        assert.strictEqual(child, bChild);
    });

    it('finds child by name', function () {
        var child = findChild('a child', children, childrenInfo);
        assert.strictEqual(child, aChild);
    });

    it('returns undefined when no matching child found', function () {
        var child = findChild('no child', children, childrenInfo);
        assert.strictEqual(child, undefined);
    });
});