/**
 * @fileoverview Config to enable all rules.
 * @author Robert Fletcher
 */

"use strict";

//------------------------------------------------------------------------------
// Requirements
//------------------------------------------------------------------------------

var load = require("../lib/load-rules"),
    Rules = require("../lib/rules");
var rules = new Rules();

//------------------------------------------------------------------------------
// Helpers
//------------------------------------------------------------------------------

var enabledRules = Object.keys(load()).reduce(function (result, ruleId) {
    if (!rules.get(ruleId).meta.deprecated) {
        result[ruleId] = "error";
    }
    return result;
}, {});

//------------------------------------------------------------------------------
// Public Interface
//------------------------------------------------------------------------------

module.exports = { rules: enabledRules };