#!/usr/bin/env node


'use strict';

var pkg = require('./package.json');
require('please-upgrade-node')(pkg);

var cmdline = require('commander');
var debugLib = require('debug');

var debug = debugLib('lint-staged:bin');

cmdline.version(pkg.version).option('-c, --config [path]', 'Path to configuration file').option('-d, --debug', 'Enable debug mode').parse(process.argv);

if (cmdline.debug) {
  debugLib.enable('lint-staged*');
}

debug('Running `lint-staged@%s`', pkg.version);

require('./src')(console, cmdline.config, cmdline.debug);